import { useEffect, useState } from "react";

const Modal_change_network = (props) => {
    if (props.chain_id) {
        return (
            <>
                {props.chain_id != 80002 ? ( // showFlagがtrueだったらModalを表示する
                    <div id="overlay" class="stars" style={{}}>
                        <div
                            id="modalContent"
                            style={{
                                color: "white",
                                position: "fixed",
                                top: "50%" /* 要素の上端を画面の中央に */,
                                left: "50%",
                                transform: "translate(-50%, -50%)",
                            }}
                        >
                            {/* 文字を白くする */}
                            <h2>私たちの利用するamoyに接続してください</h2>
                            <br />
                            <button
                                type="button"
                                class="btn btn-dark"
                                onClick={() => {
                                    props.cont.change_network();
                                }}
                            >
                                Change Network
                            </button>
                        </div>
                    </div>
                ) : (
                    <></> // showFlagがfalseの場合はModalは表示しない
                )}
            </>
        );
    } else {
        return <></>;
    }
};

export default Modal_change_network;
